// --- This Code Node extracts 'content' from multiple input items ---

// Step 1: Use .map() to iterate over ALL incoming items ($input.all()).
// For each item, access its 'json' property, and then the 'content' field within that.
const contentArray = $input.all().map(item => {
  // Basic safety check: ensure item.json and item.json.content exist.
  // Return null or an empty string if not found, otherwise return the content.
  // Adjust the fallback value (null) if needed.
  return item.json?.content ?? null;
});

// Step 2: Filter out any potential null values if an item was missing content (optional)
// If you are certain all items will have content, you can skip this filter.
const validContentArray = contentArray.filter(content => content !== null);

// Step 3: Return the result as a *single* new n8n item.
// This item contains your final array of strings under the 'documents' key.
return [{
  json: {
    // Use validContentArray if you filtered, otherwise use contentArray
    documents: validContentArray
    // documents: contentArray // <-- Use this if you didn't filter
  }
}];

// --- Code Node to Reorder Items Based on Rerank Results (WITH METADATA) ---

// --- Step 1: Get Data from Input Nodes ---

// Get the ORIGINAL full items (with content + metadata) from the node that outputs them
// Replace 'Vector Store' with the actual name of your node that outputs the chunks
const originalFullItems = $('Trigger Dynamic Hybrid Search').all();

// Get the rerank results array from the Cohere Rerank node
const rerankOrderInfo = $input.first().json.results;

// --- Step 2: Validate Inputs ---
if (!Array.isArray(originalFullItems) || originalFullItems.length === 0) {
  throw new Error("Could not retrieve original items with metadata. Check the source node name.");
}

if (!Array.isArray(rerankOrderInfo) || rerankOrderInfo.length === 0) {
  if (originalFullItems.length !== 0) {
    throw new Error("Could not get valid rerank results from the input node.");
  }
}

// --- Step 3: Reorder the FULL Items (content + metadata) ---
let sortedItems = [];

if (rerankOrderInfo && rerankOrderInfo.length > 0 && originalFullItems && originalFullItems.length > 0) {
  sortedItems = rerankOrderInfo.map(rankInfo => {
    const originalIndex = rankInfo.index;

    // Check if the index is valid
    if (originalIndex !== undefined && originalIndex !== null && 
        originalIndex >= 0 && originalIndex < originalFullItems.length) {

      // Get the full original item (with all metadata)
      const originalItem = originalFullItems[originalIndex].json;

      // Optionally add the rerank score to the item
      return {
        ...originalItem,
        rerank_score: rankInfo.relevance_score // Cohere's relevance score
      };
    } else {
      console.error(`Error: Rerank index ${originalIndex} is invalid. Skipping.`);
      return null;
    }
  }).filter(item => item !== null);
} else {
  sortedItems = [];
  console.log("Input data is empty, resulting in empty sorted output.");
}

// --- Step 4: Return the Sorted Items ---
// Option A: Return as multiple n8n items (one per document)
return sortedItems.map(item => ({ json: item }));

// Option B: Return as single item with array (uncomment if preferred)
// return [{ json: { sortedDocuments: sortedItems } }];

// Loop over input items and extract everything before "-----Document Chunks(DC)-----"
for (const item of $input.all()) {

  const intro = "The following entities and relationships were retrived.\n\n"
  const outro = "-----How to use this data-----\n\nWhen considering relationships with timestamps:\n\nEach relationship has a \"created_at\" timestamp indicating when we acquired this knowledge. When encountering conflicting relationships, consider both the semantic content and the timestamp. Don't automatically prefer the most recently created relationships - use judgment based on the context. For time-specific queries, prioritize temporal information in the content before considering creation timestamps"

  // Get the input string - adjust this based on where your string is located
  const inputString = item.json.response; // Change 'response' to your actual field name

  // Define the substring to search for
  const separator = "-----Document Chunks(DC)-----";

  // Find the position of the separator
  const separatorIndex = inputString.indexOf(separator);

  let extractedContent = "";

  if (separatorIndex !== -1) {
    // Extract everything before the separator
    extractedContent = inputString.substring(0, separatorIndex);
  } else {
    // If separator not found, return the entire string
    extractedContent = inputString;
  }

  // Add the extracted content to the item
  item.json.response = intro + extractedContent + outro;

}

return $input.all();

