// Aggregate all chunk strings into one array and attach to the first item only
const items = $input.all();

// Build contents from items that look like { json: { content: "..." } }
const contents = items
  .map(it => it.json?.content)
  .filter(s => typeof s === 'string' && s.trim() !== '');

// Build contextual summaries array (may be undefined for non-contextual chunks)
const contextualSummaries = items
  .map(it => it.json?.contextual_summary || '')

// Use the first item as the carrier
const first = items[0] ?? { json: {} };
first.json.contents = contents;
first.json.contextualSummaries = contextualSummaries;

return [first];

// Input: OpenAI response with multiple embeddings
// Uses unnest-based batch INSERT for much better performance
// Instead of N*4 parameters, we pass 4 arrays and let PostgreSQL unnest them
const embeddings = $input.first().json.data;
const originalContents = $('Setup Chunk for Embedding1').first().json.contents;
const contextualSummaries = $('Setup Chunk for Embedding1').first().json.contextualSummaries || [];
const loopItems = $('Loop over Chunks').all();

const recordManagerId = $('Create Row in Record Manager').isExecuted
  ? $('Create Row in Record Manager').first().json.id
  : $('Search Record Manager').first().json.id;

// Build parallel arrays for unnest
const contents = [];
const metadatas = [];
const embeddingStrs = [];
const recordManagerIds = [];

for (const embeddingObj of embeddings) {
  const idx = embeddingObj.index;
  contents.push(originalContents[idx]);

  // Merge contextual_summary into metadata if available
  const metadata = { ...loopItems[idx].json.metadata };
  if (contextualSummaries[idx] && contextualSummaries[idx].trim() !== '') {
    metadata.contextual_summary = contextualSummaries[idx];
  }
  metadatas.push(JSON.stringify(metadata));

  embeddingStrs.push(`[${embeddingObj.embedding.join(',')}]`);
  recordManagerIds.push(recordManagerId);
}

// unnest-based INSERT: only 4 parameters regardless of batch size
const insertQuery = `
  INSERT INTO documents_v2 (content, metadata, embedding, record_manager_id)
  SELECT c, m::jsonb, e::vector, r
  FROM unnest($1::text[], $2::text[], $3::text[], $4::int[])
    AS t(c, m, e, r)
`;

const parameters = [contents, metadatas, embeddingStrs, recordManagerIds];

return [{
  insertQuery: insertQuery,
  parameters: parameters,
  recordCount: contents.length,
  batchInfo: `Processed ${contents.length} embeddings via unnest batch`
}];

// Function to clean Unicode escape sequences
function cleanUnicodeText(text) {
  if (typeof text !== 'string') return text;

  return text
    // Remove control characters like \u0002, \u0000, etc.
    .replace(/[\u0000-\u001F\u007F-\u009F]/g, '')
    // Clean up extra whitespace
    .replace(/\s+/g, ' ')
    .trim();
}

// Loop over input items and clean the content field
for (const item of $input.all()) {
  // Clean the content field if it exists
  if (item.json.content) {
    item.json.content = cleanUnicodeText(item.json.content);
  }

  // Optional: Clean all string fields in the item
  Object.keys(item.json).forEach(key => {
    if (typeof item.json[key] === 'string') {
      item.json[key] = cleanUnicodeText(item.json[key]);
    }
  });
}

return $input.all();

// Page-Aware Markdown Aggregation for n8n Code Node
// Use a unique delimiter that won't appear in documents
const PAGE_MARKER_START = '<<<PAGE_START:';
const PAGE_MARKER_END = '>>>';

// Get the first item to process
const firstItem = $input.first();

// Check if pages exist in the first item
if (firstItem.json.pages && Array.isArray(firstItem.json.pages)) {
  // Aggregate pages with page markers
  const aggregatedMarkdown = firstItem.json.pages.map((page, index) => {
    const pageNumber = page.index !== undefined ? page.index : index;
    const pageMarker = `${PAGE_MARKER_START}${pageNumber}${PAGE_MARKER_END}`;

    // Insert page marker at the beginning of each page's content
    return `${pageMarker}\n\n${page.markdown}`;
  }).join('\n\n');

  // Return only the first item with aggregated markdown
  return [{
    json: {
      content: aggregatedMarkdown
    }
  }];
} else {
  // Handle case where pages don't exist or aren't an array
  return [{
    json: {
      content: firstItem.json.content || ''
    }
  }];
}

// Smart Markdown Chunker for n8n Code Node (Integrated with Hierarchy Extractor + Merger)
// Configuration - adjust these values as needed
const MIN_CHUNK_SIZE = 400;
const TARGET_CHUNK_SIZE = 600;
const MAX_CHUNK_SIZE = 800; // Note: Chunks can go over this size to both avoid tiny chunks as well as account for the contextual snippet and if there is more then one heading per chunk
const MAX_HEADING_LENGTH = 200   // For heading truncation in split chunks

// Page marker configuration
const PAGE_MARKER_START = '<<<PAGE_START:';
const PAGE_MARKER_END = '>>>';

// Mappings
const MARKDOWN_FIELD = 'content'  // Field name, not the actual content
const DOCUMENT_ID_FIELD = $('Set Data').first().json.doc_id
const DOCUMENT_NAME = $('Set Data').first().json.doc_name
const DOCUMENT_SUMMARY = $input.first().json.custom_metadata.document_summary
const DOCUMENT_HEADLINE = "This chunk is from " + $input.first().json.custom_metadata.document_headline_description + ", specifically "
const DEV_MODE = false; // Enable to include debug fields for testing

// Classes
class HierarchyNode {
  constructor(title, level) {
    this.title = title;
    this.level = level;
  }
}

class MarkdownChunk {
  constructor(data) {
    this.title = data.title;
    this.level = data.level;
    this.cascading_path = data.cascading_path;
    this.content = data.content;
    this.hierarchy_stack = data.hierarchy_stack;
    this.original_level = data.original_level;
    this.is_merged = data.is_merged || false;
    this.is_split = data.is_split || false;
    this.split_part_index = data.split_part_index || null;
    this.total_split_parts = data.total_split_parts || null;
    this.original_chunk_title = data.original_chunk_title || null;
    this.original_cascading_path = data.original_cascading_path || null;
    this.merged_type = data.merged_type || null;
    this.original_chunks = data.original_chunks || null;
    this.merged_chunk_count = data.merged_chunk_count || 1;
    this.enhanced_content_raw = data.enhanced_content_raw || null;
  }
}

class SmartMarkdownChunker {
  constructor(minChunkSize, maxChunkSize, targetChunkSize, devMode = false) {
    this.MIN_CHUNK_SIZE = minChunkSize;
    this.MAX_CHUNK_SIZE = maxChunkSize;
    this.TARGET_CHUNK_SIZE = targetChunkSize;
    this.DEV_MODE = devMode;
    this._document_summary = 'No summary available';
    this._total_chunks = 0;
    this._parsed_sections = []; // Store parsed sections for hierarchy building

    // Markdown-aware separators (from LangChain approach)
    this.MARKDOWN_SEPARATORS = [
      "\n## ", "\n### ", "\n#### ", "\n##### ", "\n###### ",
      "```\n\n", "\n\n***\n\n", "\n\n---\n\n", "\n\n___\n\n",
      "\n\n", "\n", ". ", " ", ""
    ];
  }

  // STEP 1: Parse markdown by major headings (H1, H2, H3, H4, H5, H6)
  parseMarkdownSections(markdown, headingLevels = [1, 2, 3, 4, 5, 6]) {
    const lines = markdown.split('\n');
    const sections = [];
    let currentSection = null;
    let currentContent = [];
    const hierarchyStack = [];

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      const headingMatch = line.match(/^(#+)\s*(.*)$/);

      if (headingMatch) {
        const level = headingMatch[1].length;

        // Only split on specified heading levels
        if (headingLevels.includes(level)) {
          // Save previous section if it exists
          if (currentSection && currentContent.length > 0) {
            currentSection.content = currentContent.join('\n');
            sections.push(currentSection);
          }

          const originalTitle = headingMatch[2].trim();
          let displayTitle = originalTitle;

          // Truncate extremely long headings for display
          if (displayTitle.length > MAX_HEADING_LENGTH) {
            displayTitle = displayTitle.substring(0, MAX_HEADING_LENGTH - 3) + '...';
          }

          // Update hierarchy stack
          while (hierarchyStack.length >= level) {
            hierarchyStack.pop();
          }
          hierarchyStack.push(new HierarchyNode(displayTitle, level));

          currentSection = {
            title: displayTitle,
            level,
            hierarchy_stack: JSON.parse(JSON.stringify(hierarchyStack)),
            content: '',
            original_title: originalTitle
          };

          currentContent = [line];
        } else {
          // Include sub-headings in content
          currentContent.push(line);
        }
      } else {
        currentContent.push(line);
      }
    }

    // Don't forget the last section
    if (currentSection && currentContent.length > 0) {
      currentSection.content = currentContent.join('\n');
      sections.push(currentSection);
    }

    // If no major headings found, treat entire document as one section
    if (sections.length === 0) {
      sections.push({
        title: 'Document',
        level: 1,
        hierarchy_stack: [new HierarchyNode('Document', 1)],
        content: markdown,
        original_title: 'Document'
      });
    }

    return sections;
  }

  // STEP 2: Recursive markdown-aware splitting within sections
  recursiveMarkdownSplit(text, separators = null) {
    if (!separators) separators = this.MARKDOWN_SEPARATORS;
    if (text.length <= this.MAX_CHUNK_SIZE) return [text];

    for (const separator of separators) {
      if (separator === "") {
        // Character-by-character splitting as last resort
        const chunks = [];
        for (let i = 0; i < text.length; i += this.MAX_CHUNK_SIZE) {
          chunks.push(text.slice(i, i + this.MAX_CHUNK_SIZE));
        }
        return chunks;
      }

      if (text.includes(separator)) {
        const parts = text.split(separator);
        const chunks = [];
        let currentChunk = '';

        for (let i = 0; i < parts.length; i++) {
          const part = i === 0 ? parts[i] : separator + parts[i];

          if (currentChunk.length + part.length <= this.MAX_CHUNK_SIZE) {
            currentChunk += part;
          } else {
            if (currentChunk) chunks.push(currentChunk);

            // If this part is still too large, recursively split it
            if (part.length > this.MAX_CHUNK_SIZE) {
              chunks.push(...this.recursiveMarkdownSplit(part, separators.slice(1)));
            } else {
              currentChunk = part;
            }
          }
        }

        if (currentChunk) chunks.push(currentChunk);
        return chunks.filter(chunk => chunk.trim().length > 0);
      }
    }

    return [text];
  }

  splitSectionIntoChunks(section) {
    const chunks = this.recursiveMarkdownSplit(section.content);

    return chunks.map((chunkContent, index) => {
      let finalContent = chunkContent.trim();

      // For split chunks, add the original section heading with part number
      if (chunks.length > 1) {
        const headingPrefix = '#'.repeat(section.level);

        if (index === 0) {
          // For Part 1, keep original text lossless - just add (Part 1) to existing heading
          const originalHeading = `${headingPrefix} ${section.original_title} (Part ${index + 1})`;
          const lines = finalContent.split('\n');
          const firstLineHeadingMatch = lines[0].match(/^#+\s/);
          if (firstLineHeadingMatch) {
            lines[0] = originalHeading;
            finalContent = lines.join('\n');
          } else {
            finalContent = `${originalHeading}\n\n${finalContent}`;
          }
        } else {
          // For Part 2+, add the heading at the start (trimmed for length)
          let titleForHeading = section.original_title;
          const partSuffix = ` (Part ${index + 1})`;
          const maxTitleLength = MAX_HEADING_LENGTH - headingPrefix.length - partSuffix.length - 1; // -1 for space

          if (titleForHeading.length > maxTitleLength) {
            titleForHeading = titleForHeading.substring(0, maxTitleLength - 3) + '...';
          }

          const originalHeading = `${headingPrefix} ${titleForHeading}${partSuffix}`;
          finalContent = `${originalHeading}\n\n${finalContent}`;
        }
      }

      return {
        content: finalContent,
        section_title: section.title,
        section_level: section.level,
        hierarchy_stack: section.hierarchy_stack,
        original_section_title: section.original_title,
        chunk_index_in_section: index,
        total_chunks_in_section: chunks.length,
        is_split: chunks.length > 1,
        split_part_number: chunks.length > 1 ? index + 1 : null,
        size: finalContent.length
      };
    });
  }

  // STEP 3: Merge small chunks intelligently
  mergeSmallChunks(chunks) {
    const result = [];
    let i = 0;

    while (i < chunks.length) {
      const currentChunk = chunks[i];

      if (currentChunk.size >= this.MIN_CHUNK_SIZE) {
        result.push(currentChunk);
        i++;
        continue;
      }

      // Try to merge with next chunk(s)
      let mergedContent = currentChunk.content;
      let mergedSize = currentChunk.size;
      let chunksToMerge = [currentChunk];
      let j = i + 1;

      while (j < chunks.length && mergedSize < this.MIN_CHUNK_SIZE) {
        const nextChunk = chunks[j];
        const testSize = mergedSize + nextChunk.size + 2; // +2 for \n\n separator

        // Allow going over MAX_CHUNK_SIZE to avoid tiny chunks
        if (testSize <= this.MAX_CHUNK_SIZE * 1.2 || mergedSize < this.MIN_CHUNK_SIZE) {
          mergedContent += '\n\n' + nextChunk.content;
          mergedSize = testSize;
          chunksToMerge.push(nextChunk);
          j++;
        } else {
          break;
        }
      }

      // Find if any merged chunk is a split part
      const splitChunk = chunksToMerge.find(chunk => chunk.is_split);

      // Create merged chunk
      const mergedChunk = {
        content: mergedContent,
        section_title: currentChunk.section_title,
        section_level: currentChunk.section_level,
        hierarchy_stack: currentChunk.hierarchy_stack,
        original_section_title: currentChunk.original_section_title,
        chunk_index_in_section: currentChunk.chunk_index_in_section,
        total_chunks_in_section: currentChunk.total_chunks_in_section,
        is_split: splitChunk ? splitChunk.is_split : currentChunk.is_split,
        split_part_number: splitChunk ? splitChunk.split_part_number : currentChunk.split_part_number,
        is_merged: chunksToMerge.length > 1,
        merged_chunk_count: chunksToMerge.length,
        size: mergedSize,
        original_chunks: chunksToMerge.length > 1 ? chunksToMerge : null
      };

      result.push(mergedChunk);
      i = j;
    }

    return result;
  }

  // STEP 4: Add document headline to final chunks
  addDocumentHeadline(chunks, headline) {
    if (!headline) return chunks;

    return chunks.map(chunk => ({
      ...chunk,
      content: `${headline}${chunk.content}`,
      size: chunk.content.length + headline.length
    }));
  }

  // Extract page information from chunk content
  extractPageInfo(content) {
    const pageMarkerRegex = new RegExp(`${PAGE_MARKER_START.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}(\\d+)${PAGE_MARKER_END.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}`, 'g');
    const pages = [];
    let match;

    while ((match = pageMarkerRegex.exec(content)) !== null) {
      pages.push(parseInt(match[1]));
    }

    // Remove duplicates and sort
    return [...new Set(pages)].sort((a, b) => a - b);
  }

  // Clean page markers from content
  cleanPageMarkers(content) {
    const pageMarkerRegex = new RegExp(`${PAGE_MARKER_START.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}\\d+${PAGE_MARKER_END.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}\\s*\\n?\\n?`, 'g');
    return content.replace(pageMarkerRegex, '').trim();
  }

  // Create metadata for chunks
  createChunkMetadata(chunks) {
    let currentPage = 0; // Start with page 0 (which is page 1 in human terms)

    return chunks.map((chunk, index) => {
      // Extract page information before cleaning
      const pagesInChunk = this.extractPageInfo(chunk.content);

      // Determine all pages this chunk spans
      let chunkPages = [];

      if (pagesInChunk.length > 0) {
        // This chunk contains page markers - include all pages found + current page
        const allPages = [currentPage, ...pagesInChunk];
        chunkPages = [...new Set(allPages)].sort((a, b) => a - b);
        // Update current page to the highest page found
        currentPage = Math.max(...pagesInChunk);
      } else {
        // No page markers in this chunk - inherit current page
        chunkPages = [currentPage];
      }

      // Clean the content of page markers
      const cleanContent = this.cleanPageMarkers(chunk.content);
      chunk.content = cleanContent;

      const metadata = {
        chunk_index: index,
        content_length: cleanContent.length,
        pages: chunkPages.map(p => p + 1) // Convert to human-readable page numbers (1, 2, 3...)
      };

      // Add section information for context
      metadata.cascading_path = chunk.hierarchy_stack ?
        chunk.hierarchy_stack.map(node => node.title).join(' > ') :
        chunk.section_title;

      // Add split part information if applicable
      if (chunk.is_split && chunk.split_part_number) {
        metadata.split_part_number = chunk.split_part_number;
        metadata.total_chunks_in_section = chunk.total_chunks_in_section;
      }

      if (this.DEV_MODE) {
        // Size analysis
        metadata.meets_size_requirements = chunk.size >= this.MIN_CHUNK_SIZE && chunk.size <= this.MAX_CHUNK_SIZE;
        metadata.size_category = chunk.size < this.MIN_CHUNK_SIZE ? 'small' :
          chunk.size > this.MAX_CHUNK_SIZE ? 'large' : 'optimal';

        // Required dev fields
        metadata.is_merged = chunk.is_merged || false;
        metadata.is_split = chunk.is_split || false;
        metadata.merged_chunk_count = chunk.merged_chunk_count || 1;
        metadata.section_level = chunk.section_level;
        metadata.section_title = chunk.section_title;

        // Split part information
        if (chunk.is_split && chunk.split_part_number) {
          metadata.split_part_number = chunk.split_part_number;
        }

        // Additional dev info
        if (chunk.is_split) {
          metadata.chunk_index_in_section = chunk.chunk_index_in_section;
        }

        if (chunk.original_chunks) {
          metadata.original_chunks_info = chunk.original_chunks.map(c => ({
            section_title: c.section_title,
            size: c.size,
            is_split: c.is_split
          }));
        }
      }

      return {
        chunk: chunk.content,
        chunk_metadata: metadata
      };
    });
  }

  // Main processing method - implements the 4-step approach
  chunkText(markdownContent) {
    try {
      console.log('Starting 4-step markdown chunking process...');

      // STEP 1: Parse markdown by major headings (H1, H2, H3, H4, H5, H6)
      const sections = this.parseMarkdownSections(markdownContent, [1, 2, 3, 4, 5, 6]);
      console.log(`Step 1 - Parsed into ${sections.length} major sections`);

      // Store parsed sections for later hierarchy building
      this._parsed_sections = sections.map((section, index) => ({
        sectionIndex: index,
        title: section.title,
        originalTitle: section.original_title,
        level: section.level,
        hierarchyStack: section.hierarchy_stack,
        hierarchyPath: section.hierarchy_stack.map(node => node.title).join(' > '),
        contentLength: section.content.length
      }));

      // STEP 2: Recursive markdown-aware splitting within sections
      let allChunks = [];
      for (const section of sections) {
        const sectionChunks = this.splitSectionIntoChunks(section);
        allChunks.push(...sectionChunks);
      }
      console.log(`Step 2 - Split into ${allChunks.length} chunks`);

      // STEP 3: Merge small chunks intelligently
      const mergedChunks = this.mergeSmallChunks(allChunks);
      console.log(`Step 3 - After merging: ${mergedChunks.length} chunks`);

      // STEP 4: Add document headline (only if contextual_embedding_enabled is false)
      const contextualEmbeddingEnabled = $('Set Data').first().json.contextual_embedding_enabled;
      const headlineToAdd = contextualEmbeddingEnabled ? null : DOCUMENT_HEADLINE;
      const finalChunks = this.addDocumentHeadline(mergedChunks, headlineToAdd);
      console.log(`Step 4 - Added document headline to ${finalChunks.length} chunks (contextual_embedding_enabled: ${contextualEmbeddingEnabled})`);


      // Generate statistics
      const sizeStats = finalChunks.map(chunk => chunk.size);
      const tooSmall = sizeStats.filter(size => size < this.MIN_CHUNK_SIZE).length;
      const tooLarge = sizeStats.filter(size => size > this.MAX_CHUNK_SIZE).length;
      const optimal = sizeStats.filter(size => size >= this.MIN_CHUNK_SIZE && size <= this.MAX_CHUNK_SIZE).length;
      console.log(`Size distribution - Too small: ${tooSmall}, Optimal: ${optimal}, Too large: ${tooLarge}`);

      // Set total chunks
      this._total_chunks = finalChunks.length;

      return this.createChunkMetadata(finalChunks);

    } catch (error) {
      return [{
        error: true,
        message: error.message,
        stack: error.stack
      }];
    }
  }

  // ========================================================================
  // INTEGRATED HIERARCHY EXTRACTION (formerly in Document Hierarchy Extractor)
  // ========================================================================

  // Map parsed sections to their corresponding chunk ranges using the final chunks
  mapSectionsToChunks(chunks) {
    const sections = this._parsed_sections;
    if (!sections || sections.length === 0) return [];
    if (!chunks || !Array.isArray(chunks)) {
      return sections.map(section => ({
        ...section,
        chunkRange: null,
        chunkCount: 0,
        chunkIndices: []
      }));
    }

    // Sort chunks by index for sequential processing
    const sortedChunks = [...chunks].sort((a, b) => a.metadata.chunk_index - b.metadata.chunk_index);

    // STEP 1: Single pass - map each section to its chunk index
    const sectionToChunkMap = new Map();
    let searchStartIndex = 0;

    sections.forEach(section => {
      const searchTitle = section.originalTitle || section.title;
      const headingPattern = `${'#'.repeat(section.level)} ${searchTitle}`;

      const foundChunks = [];

      for (let i = searchStartIndex; i < sortedChunks.length; i++) {
        const chunk = sortedChunks[i];
        const chunkContent = chunk.content || '';

        if (chunkContent.includes(headingPattern)) {
          foundChunks.push(chunk.metadata.chunk_index);

          if (i > searchStartIndex) {
            searchStartIndex = i;
          }

          // Look for additional split parts
          for (let j = i + 1; j < sortedChunks.length; j++) {
            const nextChunk = sortedChunks[j];
            const nextChunkContent = nextChunk.content || '';

            if (nextChunkContent.includes(headingPattern) ||
                (nextChunk.metadata.split_part_number &&
                 nextChunk.metadata.total_chunks_in_section &&
                 nextChunkContent.includes(section.title))) {
              foundChunks.push(nextChunk.metadata.chunk_index);

              if (nextChunk.metadata.total_chunks_in_section) {
                const totalParts = nextChunk.metadata.total_chunks_in_section;
                if (foundChunks.length >= totalParts) {
                  break;
                }
              }
            } else {
              break;
            }
          }

          break;
        }
      }

      if (foundChunks.length > 0) {
        sectionToChunkMap.set(section.sectionIndex, foundChunks);
      }
    });

    // STEP 2: Add mapped chunk index to each section
    const sectionsWithChunks = sections.map(section => {
      const mappedChunks = sectionToChunkMap.get(section.sectionIndex);

      if (!mappedChunks || mappedChunks.length === 0) {
        return {
          ...section,
          mappedChunkIndex: null,
          chunkRange: null,
          chunkCount: 0,
          chunkIndices: []
        };
      }

      const allChunks = Array.isArray(mappedChunks) ? mappedChunks : [mappedChunks];

      return {
        ...section,
        mappedChunkIndex: allChunks[0],
        chunkRange: null,
        chunkCount: allChunks.length,
        chunkIndices: allChunks.sort((a, b) => a - b)
      };
    });

    // STEP 3: Calculate hierarchical chunk ranges based on document structure
    const sectionsWithRanges = sectionsWithChunks.map((section, index) => {
      if (!section.chunkIndices || section.chunkIndices.length === 0) {
        return section;
      }

      const startChunk = Math.min(...section.chunkIndices);
      let endChunk = Math.max(...section.chunkIndices);

      // Look ahead to find all chunks under this section until we hit a same-or-higher level
      for (let i = index + 1; i < sectionsWithChunks.length; i++) {
        const nextSection = sectionsWithChunks[i];

        if (nextSection.level <= section.level) {
          break;
        }

        if (nextSection.chunkIndices && nextSection.chunkIndices.length > 0) {
          const nextSectionMaxChunk = Math.max(...nextSection.chunkIndices);
          endChunk = Math.max(endChunk, nextSectionMaxChunk);
        }
      }

      return {
        ...section,
        chunkRange: [startChunk, endChunk]
      };
    });

    // STEP 4: Add parent ranges
    return sectionsWithRanges.map((section, index) => {
      let parentRange = null;

      for (let i = index - 1; i >= 0; i--) {
        const potentialParent = sectionsWithRanges[i];

        if (potentialParent.level < section.level) {
          if (potentialParent.chunkRange) {
            parentRange = potentialParent.chunkRange;
          }
          break;
        }
      }

      return {
        ...section,
        parentRange: parentRange
      };
    });
  }

  // Build hierarchical index string from sections with chunk ranges
  buildHierarchicalIndex(sectionsWithChunks) {
    const lines = ['Document Hierarchy with Chunk Index Ranges', ''];

    sectionsWithChunks.forEach(section => {
      const hashes = '#'.repeat(section.level);
      let chunkInfo = '';

      if (section.chunkRange) {
        const [start, end] = section.chunkRange;
        chunkInfo = start === end ? ` [${start}]` : ` [${start}-${end}]`;
      } else {
        chunkInfo = ' [no chunks]';
      }

      lines.push(`${hashes} ${section.title}${chunkInfo}`);
    });

    return lines.join('\n');
  }

  // ========================================================================
  // INTEGRATED MERGER LOGIC (formerly in Merger node)
  // ========================================================================

  // Merge section range data into chunk metadata (adds childRange and parentRange)
  mergeHierarchyIntoChunks(sectionsWithChunks, chunks) {
    // Create a map of chunk_index -> sections that reference it
    const chunkToSectionsMap = new Map();

    sectionsWithChunks.forEach(section => {
      if (section.chunkIndices && Array.isArray(section.chunkIndices)) {
        section.chunkIndices.forEach(chunkIndex => {
          if (!chunkToSectionsMap.has(chunkIndex)) {
            chunkToSectionsMap.set(chunkIndex, []);
          }
          chunkToSectionsMap.get(chunkIndex).push(section);
        });
      }
    });

    // Process each chunk and add section range metadata
    return chunks.map(chunk => {
      const chunkIndex = chunk.metadata.chunk_index;
      const sectionsForChunk = chunkToSectionsMap.get(chunkIndex) || [];

      let childRange = null;
      let parentRange = null;

      if (sectionsForChunk.length > 0) {
        // Find absolute lowest and highest section ranges
        const allSectionRanges = sectionsForChunk
          .filter(section => section.chunkRange && Array.isArray(section.chunkRange))
          .map(section => section.chunkRange);

        if (allSectionRanges.length > 0) {
          const allStarts = allSectionRanges.map(range => range[0]);
          const allEnds = allSectionRanges.map(range => range[1]);
          childRange = [Math.min(...allStarts), Math.max(...allEnds)];
        }

        // Find absolute lowest and highest parent ranges
        const allParentRanges = sectionsForChunk
          .filter(section => section.parentRange && Array.isArray(section.parentRange))
          .map(section => section.parentRange);

        if (allParentRanges.length > 0) {
          const allParentStarts = allParentRanges.map(range => range[0]);
          const allParentEnds = allParentRanges.map(range => range[1]);
          parentRange = [Math.min(...allParentStarts), Math.max(...allParentEnds)];
        }
      }

      // Return chunk with enhanced metadata
      return {
        ...chunk,
        metadata: {
          ...chunk.metadata,
          childRange: childRange,
          parentRange: parentRange
        }
      };
    });
  }

  setDocumentSummary(summary) {
    this._document_summary = summary;
  }

  getTotalChunks() {
    return this._total_chunks;
  }
}

