// Markdown Hierarchy Extractor for n8n Code Node
// This tool extracts document hierarchy from markdown content independently of chunking

// Input Mappings - adjust these to match your n8n workflow
const MARKDOWN_FIELD = 'content';  // Field name containing the original markdown text
const CHUNKS_FIELD = 'chunks';     // Field name containing processed chunks (optional)

// Data source mappings - get actual data from n8n nodes
const MARKDOWN_SOURCE = () => $('Set up Text for Embedding').first().json.content;
const CHUNKS_SOURCE = () => $input.first().json.chunks;

class HierarchyNode {
  constructor(title, level) {
    this.title = title;
    this.level = level;
  }
}

class MarkdownHierarchyExtractor {
  constructor(maxHeadingLength = 200) {
    this.MAX_HEADING_LENGTH = maxHeadingLength;
  }

  // Extract clean document hierarchy from markdown
  extractHierarchy(markdownContent, headingLevels = [1, 2, 3, 4, 5, 6]) {
    const lines = markdownContent.split('\n');
    const sections = [];
    let currentSection = null;
    let currentContent = [];
    const hierarchyStack = [];

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      const headingMatch = line.match(/^(#+)\s*(.*)$/);

      if (headingMatch) {
        const level = headingMatch[1].length;

        // Only process specified heading levels
        if (headingLevels.includes(level)) {
          // Save previous section if it exists
          if (currentSection && currentContent.length > 0) {
            currentSection.content = currentContent.join('\n');
            currentSection.contentLength = currentSection.content.length;
            sections.push(currentSection);
          }

          const originalTitle = headingMatch[2].trim();
          let displayTitle = originalTitle;

          // Truncate extremely long headings for display
          if (displayTitle.length > this.MAX_HEADING_LENGTH) {
            displayTitle = displayTitle.substring(0, this.MAX_HEADING_LENGTH - 3) + '...';
          }

          // Update hierarchy stack
          while (hierarchyStack.length >= level) {
            hierarchyStack.pop();
          }
          hierarchyStack.push(new HierarchyNode(displayTitle, level));

          currentSection = {
            title: displayTitle,
            originalTitle: originalTitle,
            level,
            hierarchyStack: JSON.parse(JSON.stringify(hierarchyStack)),
            hierarchyPath: hierarchyStack.map(node => node.title).join(' > '),
            content: '',
            contentLength: 0,
            sectionIndex: sections.length
          };

          currentContent = [line];
        } else {
          // Include sub-headings in content
          currentContent.push(line);
        }
      } else {
        currentContent.push(line);
      }
    }

    // Don't forget the last section
    if (currentSection && currentContent.length > 0) {
      currentSection.content = currentContent.join('\n');
      currentSection.contentLength = currentSection.content.length;
      sections.push(currentSection);
    }

    // If no major headings found, treat entire document as one section
    if (sections.length === 0) {
      sections.push({
        title: 'Document',
        originalTitle: 'Document',
        level: 1,
        hierarchyStack: [new HierarchyNode('Document', 1)],
        hierarchyPath: 'Document',
        content: markdownContent,
        contentLength: markdownContent.length,
        sectionIndex: 0
      });
    }

    return sections;
  }



  // Helper method to get consecutive chunks from a starting point
  getConsecutiveChunks(chunks, startIndex) {
    if (!chunks || chunks.length === 0 || startIndex >= chunks.length) {
      return [];
    }

    const result = [chunks[startIndex]];
    const startChunkIndex = chunks[startIndex].metadata.chunk_index;

    // Look for consecutive chunks after the starting chunk
    for (let i = startIndex + 1; i < chunks.length; i++) {
      const currentChunkIndex = chunks[i].metadata.chunk_index;
      const expectedNextIndex = result[result.length - 1].metadata.chunk_index + 1;

      if (currentChunkIndex === expectedNextIndex) {
        result.push(chunks[i]);
      } else {
        // Break on first non-consecutive chunk
        break;
      }
    }

    return result;
  }

  // Map sections to their corresponding chunk ranges
  mapSectionsToChunks(sections, chunks) {
    if (!chunks || !Array.isArray(chunks)) {
      return sections.map(section => ({
        ...section,
        chunkRange: null,
        chunkCount: 0
      }));
    }

    // Sort chunks by index for sequential processing
    const sortedChunks = [...chunks].sort((a, b) => a.metadata.chunk_index - b.metadata.chunk_index);

    // STEP 1: Single pass - map each section to its chunk index
    const sectionToChunkMap = new Map(); // sectionIndex -> chunkIndex
    let searchStartIndex = 0; // Only search forward, never backward
    const usedChunks = new Set(); // Track which chunks have been fully processed

    console.log('=== SINGLE PASS MAPPING ===');

    sections.forEach(section => {
      // Use the original title (not truncated) for searching in chunks
      const searchTitle = section.originalTitle || section.title;
      const headingPattern = `${'#'.repeat(section.level)} ${searchTitle}`;

      // Search for this section's heading starting from searchStartIndex
      const foundChunks = [];

      for (let i = searchStartIndex; i < sortedChunks.length; i++) {
        const chunk = sortedChunks[i];
        const chunkContent = chunk.content || '';

        if (chunkContent.includes(headingPattern)) {
          foundChunks.push(chunk.metadata.chunk_index);

          // If we found a match beyond the current search start, advance the search start
          if (i > searchStartIndex) {
            searchStartIndex = i;
          }

          // Look for additional split parts by checking for the section heading in subsequent chunks
          for (let j = i + 1; j < sortedChunks.length; j++) {
            const nextChunk = sortedChunks[j];
            const nextChunkContent = nextChunk.content || '';

            // Check if next chunk contains our section heading (indicating it's a split part)
            if (nextChunkContent.includes(headingPattern) || 
                (nextChunk.metadata.split_part_number && 
                 nextChunk.metadata.total_chunks_in_section &&
                 nextChunkContent.includes(section.title))) {
              foundChunks.push(nextChunk.metadata.chunk_index);

              // If this chunk has split metadata, use it to determine how many more parts to look for
              if (nextChunk.metadata.total_chunks_in_section) {
                const totalParts = nextChunk.metadata.total_chunks_in_section;
                if (foundChunks.length >= totalParts) {
                  break;
                }
              }
            } else {
              // Stop if we don't find a related chunk
              break;
            }
          }

          break;
        }
      }

      if (foundChunks.length > 0) {
        // Store all chunks for this section
        sectionToChunkMap.set(section.sectionIndex, foundChunks);
        console.log(`Section ${section.sectionIndex}: "${section.title}"  Chunks [${foundChunks.join(',')}]`);
      } else {
        console.log(`Section ${section.sectionIndex}: "${section.title}"  NOT FOUND`);
      }
    });

    console.log('=== END SINGLE PASS MAPPING ===');

    // STEP 2: Add mapped chunk index to each section (keep chunkIndices as-is for splits)
    const sectionsWithChunks = sections.map(section => {
      const mappedChunks = sectionToChunkMap.get(section.sectionIndex);

      if (!mappedChunks || mappedChunks.length === 0) {
        return {
          ...section,
          mappedChunkIndex: null,
          chunkRange: null,
          chunkCount: 0,
          chunkIndices: []
        };
      }

      // For sections with multiple chunks (splits), use all of them
      const allChunks = Array.isArray(mappedChunks) ? mappedChunks : [mappedChunks];

      return {
        ...section,
        mappedChunkIndex: allChunks[0], // First chunk index for backward compatibility
        chunkRange: null, // Will be calculated in step 3
        chunkCount: allChunks.length,
        chunkIndices: allChunks.sort((a, b) => a - b)
      };
    });

    // STEP 3: Calculate hierarchical chunk ranges based on document structure
    const sectionsWithRanges = sectionsWithChunks.map((section, index) => {
      if (!section.chunkIndices || section.chunkIndices.length === 0) {
        return section;
      }

      // Find the range of chunks that belong to this section hierarchically
      const startChunk = Math.min(...section.chunkIndices);
      let endChunk = Math.max(...section.chunkIndices);

      // Look ahead to find all chunks under this section until we hit a same-or-higher level
      for (let i = index + 1; i < sectionsWithChunks.length; i++) {
        const nextSection = sectionsWithChunks[i];

        // If we hit a section at the same level or higher, stop
        if (nextSection.level <= section.level) {
          break;
        }

        // If the next section has chunks, extend our range to include them
        if (nextSection.chunkIndices && nextSection.chunkIndices.length > 0) {
          const nextSectionMaxChunk = Math.max(...nextSection.chunkIndices);
          endChunk = Math.max(endChunk, nextSectionMaxChunk);
        }
      }

      return {
        ...section,
        chunkRange: [startChunk, endChunk]
      };
    });

    // STEP 4: Add parent ranges
    return sectionsWithRanges.map((section, index) => {
      let parentRange = null;

      // Find the immediate parent (first section with lower level going backwards)
      for (let i = index - 1; i >= 0; i--) {
        const potentialParent = sectionsWithRanges[i];

        if (potentialParent.level < section.level) {
          // Found the immediate parent
          if (potentialParent.chunkRange) {
            parentRange = potentialParent.chunkRange;
          }
          break;
        }
      }

      return {
        ...section,
        parentRange: parentRange
      };
    });
  }

  // Build hierarchical index with section ranges
  buildHierarchicalIndex(sectionsWithChunks) {
    const lines = ['Document Hierarchy with Chunk Index Ranges', ''];

    sectionsWithChunks.forEach(section => {
      const hashes = '#'.repeat(section.level);
      let chunkInfo = '';

      if (section.chunkRange) {
        const [start, end] = section.chunkRange;
        chunkInfo = start === end ? ` [${start}]` : ` [${start}-${end}]`;
      } else {
        chunkInfo = ' [no chunks]';
      }

      lines.push(`${hashes} ${section.title}${chunkInfo}`);
    });

    return lines.join('\n');
  }

}

