// Chunk-Section Merger for n8n Code Node
// This tool merges sections and chunks data, adding section ranges to chunk metadata

// Input Mappings - adjust these to match your n8n workflow
const SECTIONS_SOURCE = () => $('Document Hierarchy Extractor').first().json.sections;
const CHUNKS_SOURCE = () => $('Smart Chunker').first().json.chunks;

class ChunkSectionMerger {
  constructor() {
    // No configuration needed
  }

  // Merge sections data into chunks metadata
  mergeData(sections, chunks) {
    if (!sections || !Array.isArray(sections)) {
      throw new Error('Invalid sections data provided');
    }

    if (!chunks || !Array.isArray(chunks)) {
      throw new Error('Invalid chunks data provided');
    }

    // Create a map of chunk_index -> sections that reference it
    const chunkToSectionsMap = new Map();

    // Process each section to build the mapping
    sections.forEach(section => {
      if (section.chunkIndices && Array.isArray(section.chunkIndices)) {
        section.chunkIndices.forEach(chunkIndex => {
          if (!chunkToSectionsMap.has(chunkIndex)) {
            chunkToSectionsMap.set(chunkIndex, []);
          }
          chunkToSectionsMap.get(chunkIndex).push(section);
        });
      }
    });

    // Process each chunk and add section range metadata
    return chunks.map(chunk => {
      const chunkIndex = chunk.metadata.chunk_index;
      const sectionsForChunk = chunkToSectionsMap.get(chunkIndex) || [];

      let sectionRange = null;
      let parentRange = null;

      if (sectionsForChunk.length > 0) {
        // Find absolute lowest and highest section ranges
        const allSectionRanges = sectionsForChunk
          .filter(section => section.sectionRange && Array.isArray(section.sectionRange))
          .map(section => section.sectionRange);

        if (allSectionRanges.length > 0) {
          const allStarts = allSectionRanges.map(range => range[0]);
          const allEnds = allSectionRanges.map(range => range[1]);
          sectionRange = [Math.min(...allStarts), Math.max(...allEnds)];
        }

        // Find absolute lowest and highest parent ranges
        const allParentRanges = sectionsForChunk
          .filter(section => section.parentRange && Array.isArray(section.parentRange))
          .map(section => section.parentRange);

        if (allParentRanges.length > 0) {
          const allParentStarts = allParentRanges.map(range => range[0]);
          const allParentEnds = allParentRanges.map(range => range[1]);
          parentRange = [Math.min(...allParentStarts), Math.max(...allParentEnds)];
        }
      }

      // Return chunk with enhanced metadata
      return {
        ...chunk,
        metadata: {
          ...chunk.metadata,
          childRange: sectionRange,
          parentRange: parentRange
        }
      };
    });
  }
}

